class CatOrderby implements \Magento\Framework\Option\ArrayInterface
{
    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return [
            ['value' => 1, 'label' => __('Newest first')],
            ['value' => 2, 'label' => __('Older first')],
            ['value' => 3, 'label' => __('Position: High to Low')],
            ['value' => 4, 'label' => __('Position: Low to High')]
        ];
    }
}
